import React, { useRef, useState, useEffect } from "react";
import "./projects.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faarrowright } from "@fortawesome/free-solid-svg-icons";
import imges from "./img.jpg";
import $ from "jquery";
import axios from "axios";
console.log("Hello World");

const Projects = () => {
  const scrl = useRef();
  const [scrollX, setscrollX] = useState(0); // For detecting start scroll postion
  const [scrolEnd, setscrolEnd] = useState(false); // For detecting end of scrolling
  const [isLoading, setisLoading] = useState(true); // For detecting end of scrolling

  const slide = (shift) => {
    scrl.current.scrollLeft += shift;
    setscrollX(scrollX + shift);
    //For checking if the scroll has ended
    if (
      Math.floor(scrl.current.scrollWidth - scrl.current.scrollLeft) <=
      scrl.current.offsetWidth
    ) {
      setscrolEnd(true);
    } else {
      setscrolEnd(false);
    }
  };

  const scrollCheck = () => {
    setscrollX(scrl.current.scrollLeft);
    if (
      Math.floor(scrl.current.scrollWidth - scrl.current.scrollLeft) <=
      scrl.current.offsetWidth
    ) {
      setscrolEnd(true);
    } else {
      setscrolEnd(false);
    }
  };
  useEffect(() => {
    //Check width of the scollings
    if (
      scrl.current &&
      scrl?.current?.scrollWidth === scrl?.current?.offsetWidth
    ) {
      setscrolEnd(true);
    } else {
      setscrolEnd(false);
    }
    return () => {};
  }, [scrl?.current?.scrollWidth, scrl?.current?.offsetWidth]);

  const [projects, setProjects] = useState([]);

  useEffect(() => {
    axios
      .get("projects/")
      .then((res) => {
        setProjects((prev) => [...prev, ...res.data["Projects"]]);
        // console.log(projects[0]);
        setisLoading(false);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
  if (isLoading) {
    return <div>Loading...</div>;
  }
  // console.log
  return (
    <div>
      <div className="sec container">
        <h4 className="display-7">Projects</h4>
        <hr className="hr" />
        <div className="proj-wrapper">
          {scrollX !== 0 && (
            <button className="swipe-left" onClick={() => slide(-320)}>
              <i className="fa fa-angle-left"></i>
            </button>
          )}
          <div
            id="projects"
            className="projects container-fluid"
            ref={scrl}
            onScroll={scrollCheck}
          >

            {(projects != []) &&
              (<div className="project">
                <img className="project-pic" src={projects[0]["image"]} />
                <h5>ChatApp</h5>
                <div className="texts">
                  lorem ipsum dolor sit amet, consectetur adipiscing lorem lorem
                  ipsum dolor sit amet lorem ipsum dolor sit amet, consectetur
                  adipiscing lorem lorem ipsum dolor sit amet
                </div>
              </div>)
            }
          </div>
          {!scrolEnd && (
            <button className="swipe-right" onClick={() => slide(320)}>
              <i className="fa fa-angle-right"></i>{" "}
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default Projects;
